package alipayclient

import (
	"github.com/smartwalle/alipay/v3"
	"stock-web-be/gocommon/consts"
	"stock-web-be/gocommon/tlog"
)

var client *alipay.Client

func GetAlipayClient() *alipay.Client {
	return client
}

const (
	APPID = "2021003189689338"
)

func Init() {
	// 创建支付宝客户端实例
	client, _ = alipay.New(APPID,
		"",
		true)

	client.LoadAppPublicCert("")
	client.LoadAliPayPublicKey("")

	// 构造交易参数
	client.SetEncryptKey("ThlHClWx+ktjKHqG+fSMMg==")
	tlog.Handler.Infof(nil, consts.SLTagAlipaySuccess, "alipay connect success")
}
